import React, { useState, useContext } from "react";
import FormInput from "../../components/form-input/form-input.component.jsx";
import UserContext from "../../context/user.context.js";
import Button from "../../components/button/button.component.jsx";
import { withRouter } from "react-router-dom";
import APP_PATHS from "../../APP_PATHS.js";
import { SOCKET_ADDRESS } from "../../APP_CONFIG.js";

const LoginPage = ({ history, currentUser: user }) => {
  if (user) history.push(APP_PATHS.home);

  const [formData, setFormData] = useState({
    email: "",
    password: ""
  });

  async function postData(url = '', data = {}) {
    // Default options are marked with *
    const response = await fetch(url, {
      method: 'POST', // *GET, POST, PUT, DELETE, etc.
      mode: 'cors', // no-cors, *cors, same-origin
      cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
      credentials: 'same-origin', // include, *same-origin, omit
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      redirect: 'follow', // manual, *follow, error
      referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
      body: JSON.stringify(data) // body data type must match "Content-Type" header
    });
    return response.json(); // parses JSON response into native JavaScript objects
  }

  const [currentUser, setCurrentUser] = useState(user);

  const handleSubmit = async (event) => {
    event.preventDefault();
    // fetch api endpoint
    const jsonData = {email: "user@email.com", password: "password"};
    const formatData = new URLSearchParams(Object.entries(jsonData));
    const data = await postData(`${SOCKET_ADDRESS}${APP_PATHS.login}`, jsonData);
    console.log("USER DATA:\n", data);
    history.push(APP_PATHS.home);
  };

  const handleInput = (event) => {
    const { name, value } = event.target;

    setFormData({
      ...formData,
      [name]: value
    });
  };

  return (
    <div id="login-page" className="page">
      <form
        id="login-form"
        onSubmit={handleSubmit}>
        <FormInput
          id="login-email"
          type="email"
          name="email"
          value={formData.email}
          label="Email"
          required
          onChange={handleInput} />
        <br/>
        <FormInput
          id="login-pass"
          type="password"
          name="password"
          value={formData.password}
          label="Password"
          required
          onChange={handleInput} />
        <br />
        <Button type="submit">Submit</Button>
      </form>
    </div>
  );
};

export default withRouter(LoginPage);
