import React, { useState, useEffect, useContext } from "react";
import UserContext from "./context/user.context.js";

import './App.css';
import { Router, Route, Switch, Redirect } from "react-router-dom";

import APP_PATHS from "./APP_PATHS.js";

// Components
import Header from "./components/header/header.component.jsx";

// Pages
import HomePage from "./pages/home/home.page.jsx";
import LoginPage from "./pages/login/login.page.jsx"
import RegisterPage from "./pages/register/register.page.jsx";

const App = () => {
  const [currentUser, setCurrentUser] = useState(useContext(UserContext));

  useEffect(() => {
    (async () => {
      // Swap with API call after it is implemented
      const loggedIn = true;
      if (loggedIn) {
        setCurrentUser({
          name: "Alex",
          email: "a@gmail.com",
          password: "aeCEHu374AUHi",
          passwordKey: "advhieahvia"
        })
      } else {
        setCurrentUser(null);
      }
    })();
  }, []);

  const accessPageWithoutLoginOnly = (component) => {
    if (currentUser) {
      return (<Redirect to={APP_PATHS.home} />);
    } else {
      return component;
    }
  }

  const accessPageWithLoginOnly = () => {

  }

  return (
    <UserContext.Provider value={currentUser}>
      <Header />
      <Switch>
        <Route exact path={APP_PATHS.home} component={HomePage} />
        {
          accessPageWithoutLoginOnly(
            <Route exact path={APP_PATHS.login} component={LoginPage} />
          )
        }
        {
          accessPageWithoutLoginOnly(
            <Route exact path={APP_PATHS.register} component={RegisterPage} />
          )
        }
      </Switch>
    </UserContext.Provider>
  );
};

export default App;
