import React, { useState, useEffect, useContext } from "react";
import UserContext from "./context/user.context.js";

import './App.css';
import { Router, Route, Switch } from "react-router-dom";

// Components
import "./components/header/header.component.jsx";

// Pages
import HomePage from "./pages/home/home.page.jsx";
import LoginPage from "./pages/login/login.page.jsx"
import RegisterPage from "./pages/register/register.page.jsx";

const App = () => {
  const [currentUser, setCurrentUser] = useState(useContext(UserContext));

  useEffect(() => {
    (async () => {
      // Swap with API call after it is implemented
      const loggedIn = false;
      if (loggedIn) setCurrentUser({
        ...currentUser,
        currentUser: {
          name: "Alex",
          email: "a@gmail.com",
          password: "aeCEHu374AUHi",
          passwordKey: "advhieahvia"
        }
      })
    })();
  }, []);

  return (
    <UserContext.Provider value={currentUser}>
      <Header />
      <Switch>
        <Route exact path="/" component={HomePage} />
        <Route exact path="/login" component={LoginPage} />
        <Route exact path="/register" component={RegisterPage} />
      </Switch>
    </UserContext.Provider>
  );
};

export default App;
